function clamp(s, n) {
  s = String(s || '');
  return s.length <= n ? s : (s.slice(0, n).trimEnd() + '‚Ä¶');
}

function isSameLocalDay(a, b) {
  const da = new Date(a);
  const db = new Date(b);
  if (Number.isNaN(da.getTime()) || Number.isNaN(db.getTime())) return false;
  return da.getFullYear() === db.getFullYear() && da.getMonth() === db.getMonth() && da.getDate() === db.getDate();
}

function fmtDate(d) {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, '0');
  const day = String(d.getDate()).padStart(2, '0');
  return `${y}-${m}-${day}`;
}

const now = new Date();
const todayKey = fmtDate(now);

// Code node output must be: [{ json: {...} }, ...]

const statsItem = ($input.all && $input.all()[0] && $input.all()[0].json) ? $input.all()[0].json : {};
const stats = (statsItem && typeof statsItem === 'object' && statsItem.body && typeof statsItem.body === 'object') ? statsItem.body : statsItem;

let tasksNodeJson;
try { tasksNodeJson = $node['Fetch Tasks']?.json; } catch (e) {}
if (!tasksNodeJson) {
  try { tasksNodeJson = $item(0).$node['Fetch Tasks']?.json; } catch (e) {}
}
if (!tasksNodeJson && typeof $items === 'function') {
  try { tasksNodeJson = ($items('Fetch Tasks')[0] || {}).json; } catch (e) {}
}

const tasksResp = (tasksNodeJson && typeof tasksNodeJson === 'object' && 'body' in tasksNodeJson) ? tasksNodeJson.body : tasksNodeJson;

let list = [];
if (Array.isArray(tasksResp)) {
  list = tasksResp;
} else if (tasksResp && typeof tasksResp === 'object' && Array.isArray(tasksResp.data)) {
  // Some setups wrap arrays as { data: [...] }
  list = tasksResp.data;
}

const doneToday = list
  .filter((t) => t && t.status === 'done' && t.updatedAt && isSameLocalDay(t.updatedAt, now))
  .sort((a, b) => String(b.updatedAt || '').localeCompare(String(a.updatedAt || '')));

const blocked = list
  .filter((t) => t && t.status === 'blocked')
  .sort((a, b) => Number(a.priority || 3) - Number(b.priority || 3));

const tomorrow = list
  .filter((t) => t && (t.status === 'ready' || t.status === 'running' || t.status === 'review'))
  .sort((a, b) => {
    const pa = Number(a.priority || 3);
    const pb = Number(b.priority || 3);
    if (pa !== pb) return pa - pb;
    return String(b.updatedAt || '').localeCompare(String(a.updatedAt || ''));
  });

const maxDone = Number($env.DAILY_WRAPUP_MAX_DONE || 8);
const maxBlocked = Number($env.DAILY_WRAPUP_MAX_BLOCKED || 6);
const maxTomorrow = Number($env.DAILY_WRAPUP_MAX_TOMORROW || 8);

function oneLineTask(t) {
  const name = clamp(t.name || '(no name)', 80);
  const id = String(t.id || '').slice(0, 8);
  return id ? `${name} (#${id})` : name;
}

function blockedLine(t) {
  const base = oneLineTask(t);
  const hint = (Array.isArray(t.nextSteps) && t.nextSteps[0]) ? String(t.nextSteps[0]) : (t.summary ? String(t.summary).split(/\r?\n/)[0] : '');
  const hint2 = clamp(hint, 80);
  return hint2 ? `${base} - ${hint2}` : base;
}

const lines = [];
lines.push(`üåô Daily Wrap-up ${todayKey}`);

// Small ops stats (cheap + helpful)
if (typeof stats.todayRuns === 'number' || typeof stats.successRate === 'number') {
  const parts = [];
  if (typeof stats.todayRuns === 'number') parts.push(`runs: ${stats.todayRuns}`);
  if (typeof stats.successRate === 'number') parts.push(`success: ${stats.successRate}%`);
  if (typeof stats.failedRuns === 'number') parts.push(`failed: ${stats.failedRuns}`);
  if (typeof stats.queueDepth === 'number') parts.push(`queue: ${stats.queueDepth}`);
  if (typeof stats.activeTasks === 'number') parts.push(`active: ${stats.activeTasks}`);
  if (parts.length) lines.push(`üìä ${parts.join(' | ')}`);
}

lines.push('');
lines.push(`‚úÖ ‰ªäÂ§©ÂÆåÊàê (${doneToday.length})`);
if (doneToday.length === 0) {
  lines.push('- (none)');
} else {
  for (const t of doneToday.slice(0, maxDone)) lines.push(`- ${oneLineTask(t)}`);
  if (doneToday.length > maxDone) lines.push(`- ‚Ä¶ +${doneToday.length - maxDone} more`);
}

lines.push('');
lines.push(`‚õî Âç°ÁÇπ/ÈòªÂ°û (${blocked.length})`);
if (blocked.length === 0) {
  lines.push('- (none)');
} else {
  for (const t of blocked.slice(0, maxBlocked)) lines.push(`- ${blockedLine(t)}`);
  if (blocked.length > maxBlocked) lines.push(`- ‚Ä¶ +${blocked.length - maxBlocked} more`);
}

lines.push('');
lines.push(`üìå ÊòéÂ§©ËÆ°Âàí (${tomorrow.length})`);
if (tomorrow.length === 0) {
  lines.push('- (none)');
} else {
  for (const t of tomorrow.slice(0, maxTomorrow)) lines.push(`- ${oneLineTask(t)}`);
  if (tomorrow.length > maxTomorrow) lines.push(`- ‚Ä¶ +${tomorrow.length - maxTomorrow} more`);
}

const apiBase = ($env.TASKBOARD_BASE_URL || $env.OPENCLAW_API_BASE || 'http://host.docker.internal:3011').replace(/\/+$/, '');
lines.push('');
lines.push(`üîó ‰ªªÂãôÊùøÔºö${apiBase}`);

const text = clamp(lines.join('\n'), 3800);
return [{ json: { text } }];
