parameters.functionCode:
const data = items[0].json;
if (!Array.isArray(data)) {
  return [];
}

// Keep a small local memory inside n8n (staticData) to avoid duplicate notifications.
const s = this.getWorkflowStaticData('global');
if (!s.seenRunIds) s.seenRunIds = {};

const now = Date.now();
const maxAgeMs = Number($env.OPENCLAW_NOTIFY_MAX_AGE_MS || 6 * 60 * 60 * 1000); // default 6h
const maxKeep = Number($env.OPENCLAW_NOTIFY_MAX_KEEP || 5000);

// prune old entries occasionally
if (!s.lastPruneAt || (now - s.lastPruneAt) > 10 * 60 * 1000) {
  const keys = Object.keys(s.seenRunIds);
  if (keys.length > maxKeep) {
    // Best-effort prune: keep newest by seenAt
    keys.sort((a, b) => (s.seenRunIds[b] || 0) - (s.seenRunIds[a] || 0));
    for (const k of keys.slice(maxKeep)) delete s.seenRunIds[k];
  }
  s.lastPruneAt = now;
}

const onlySuccess = String($env.OPENCLAW_NOTIFY_ONLY_SUCCESS || 'true') !== 'false';

const out = [];
for (const r of data) {
  if (!r || typeof r !== 'object') continue;
  const id = String(r.id || '');
  if (!id) continue;
  if (s.seenRunIds[id]) continue;

  const status = String(r.status || '');
  if (onlySuccess && status !== 'success') continue;

  const startedAt = r.startedAt ? Date.parse(r.startedAt) : NaN;
  if (!Number.isNaN(startedAt) && (now - startedAt) > maxAgeMs) continue;

  // Need runPath + taskId to be useful.
  const runPath = String(r.runPath || '');
  const taskId = String(r.taskId || '');
  if (!runPath || !taskId) continue;

  out.push({ json: { runId: id, taskId, taskName: String(r.taskName || ''), runPath, status } });
}

// Mark as seen immediately (idempotent) so retries don't spam.
for (const it of out) {
  s.seenRunIds[it.json.runId] = now;
}

return out;

const task = items[0].json;
const runId = $item(0).$node["逐筆處理"].json.runId;
const runPath = $item(0).$node["逐筆處理"].json.runPath;
const taskId = $item(0).$node["逐筆處理"].json.taskId;
const taskName = $item(0).$node["逐筆處理"].json.taskName || task?.name || '';

// Build absolute filesystem path inside n8n docker container.
// You must mount the repo into /workspace, e.g. -v "/Users/.../openclaw任務面版設計:/workspace"
const rel = String(runPath || '').replace(/^\/+/, '');
const resultPath = '/workspace/' + rel.replace(/\/+$/, '/') + 'RESULT.md';

return [{
  json: {
    taskId,
    taskName,
    runId,
    runPath,
    resultPath,
    projectPath: task?.projectPath || '',
    idempotencyKey: task?.idempotencyKey || '',
  }
}];

function clamp(s, n) {
  s = String(s || '').trim();
  if (!s) return '';
  return s.length <= n ? s : (s.slice(0, n).trimEnd() + '…');
}

const runId = $json.runId;
const taskId = $json.taskId;
const taskName = $json.taskName;
const runPath = $json.runPath;
const resultPath = $json.resultPath;

let text = '';
try {
  const b = items[0].binary?.data;
  if (b?.data) {
    text = Buffer.from(b.data, 'base64').toString('utf8');
  }
} catch (e) {
  text = '';
}

// Extract summary block if present.
// Prefer "## summary" section; otherwise fallback to first non-empty lines.
let summary = '';
if (text) {
  const m = text.match(/\n##\s*summary\s*\n([\s\S]*?)(\n##\s|$)/i);
  if (m && m[1]) summary = m[1].trim();
}
if (!summary && text) {
  const lines = text.split(/\r?\n/).map(l => l.trim()).filter(Boolean);
  summary = lines.slice(0, 8).join('\n');
}
summary = clamp(summary, 800);

// Basic nextSteps heuristics.
let nextSteps = [];
if (text) {
  const m2 = text.match(/\n##\s*(next\s*steps|nextSteps|todo|下一步)\s*\n([\s\S]*?)(\n##\s|$)/i);
  if (m2 && m2[2]) {
    nextSteps = m2[2]
      .split(/\r?\n/)
      .map(l => l.replace(/^[-*\d.\s]+/, '').trim())
      .filter(Boolean)
      .slice(0, 6);
  }
}
if (nextSteps.length === 0) {
  nextSteps = [
    '查看 RESULT.md 的完整內容與驗收清單',
    '依 acceptanceCriteria 逐項人工驗收',
    '驗收通過後將任務狀態改為 done（或建立下一張任務）'
  ];
}

const evidenceLinks = [runPath.replace(/\/+$/, '/') + 'RESULT.md'];

return [{
  json: {
    taskId,
    taskName,
    runId,
    runPath,
    resultPath,
    summary,
    nextSteps,
    evidenceLinks
  }
}];

